sys_script_include.script:
function validatePhoneNumber(phoneStr){
    // Use JavaScript coercion to guarantee phoneStr is a string
    phoneStr = phoneStr + '';
    // Compare phoneStr against the allowed syntax as specified in the regular expression
    // If phoneStr has allowed syntax, return true, else return false
    if(phoneStr.match(/^((\+91?)|\+)?[7-9][0-9]{9}$/)){
      return true;
    }
    else {
     return false;
    }
  }
